import React, {useState, useEffect} from 'react';
import './App.css';
import { type } from 'jquery';

function App() {

  const [data, setData] = useState([{}])

  useEffect(() => {
    fetch("/api/tasks")
    .then(res => res.json())
    .then(
      data => {
        setData(data)
        console.log(data)
      }
    )
  }, [])

  return (
    <div className="App">
      {(typeof data.tasks === 'undefined') ? (
        <p>Loading ...</p>
      ) : (
        data.tasks.map((task, i) => (
          <p key={i}>{task}</p>
        ))
      )}
    </div>
  );
}

export default App;
