import React, {useState, useEffect} from 'react';
import axios from 'axios'
import {format} from 'date-fns'
import './App.css';


function App() {
  const [content, setContent] = useState("");
  const [editContent, setEditContent] = useState("");
  const [taskList, setTaskList] = useState([]);
  const [taskId, setTaskId] = useState(null);

  const fetchTasks = async () => {
    const data = await axios.get('/api/tasks');
    const {tasks} = data.data;
    setTaskList(tasks);
  }

  const handleChange = (e, field) => {
    if (field === 'edit') {
      setEditContent(e.target.value)
    } else {
      setContent(e.target.value);
    }
  }

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/tasks/${id}`);
      const updatedList = taskList.filter(task => task.id !== id);
      setTaskList(updatedList);
    } catch (err) {
      console.error(err.message);
    }
  }

  const toggleEdit = (task) => {
    setTaskId(task.id);
    setEditContent(task.content);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try{
      if (editContent){
        const data = await axios.put(`/api/tasks/${taskId}`, {content: editContent});
        const updatedTask = data.data.task;
        const updatedList = taskList.map(task => {
          if(task.id === taskId){
            return task = updatedTask
          }
          return task
        })
        setTaskList(updatedList)
      } else {
        const data = await axios.post("/api/tasks", {content});
        setTaskList([...taskList, data.data]);
      }
      setContent('');
      setEditContent('');
      setTaskId(null);
    } catch (err) {
      console.error(err.message);
    }
  }

  const handleCheck = async (task) => {
    console.log("uso sam");
    const completed = !task.completed;
    try {
      await axios.put(`/api/tasks/${task.id}`, {completed});
      const updatedTasks = taskList.map(t => {
        if (t.id === task.id) {
          return {...t, completed};
        }
        return t;
      });
      setTaskList(updatedTasks);
    } catch (err) {
      console.error(err.message);
    }
  }


  useEffect(() => {
    fetchTasks();
  }, [])

  return (
    <div className="App">
     <section>
     <h1 className="center-align white-text">ToDo Application</h1>
     <form onSubmit={handleSubmit}>
       <div className="input-field">
          <label htmlFor="content">Content</label>
          <input
            onChange={(e) => handleChange(e, 'content')}
            type="text"
            name="content"
            id="content"
            value={content}
          />
        </div>
        <button className="waves-effect waves-light btn" type="submit">
          Submit
        </button>
     </form>
     </section>
     <section>
     <div className="row center-align">
        {taskList.map(task => {
          if (taskId === task.id) {
            return (
              <form onSubmit={handleSubmit} key={task.id}>
              <input
                onChange={(e) => handleChange(e, 'edit')}
                type="text"
                name="editContent"
                id="editContent"
                placeholder="Write a task. :)"
                value={editContent}
              />
              <button type='submit'>Submit</button>
              </form>
            )
          } else {
            return (
              <div className='col s12 m8 16' key={task.id}>
                <div className='card'>
                  <p>{task.content}</p>
                </div>
              </div>   
            )
          }
        })}
      </div> 
     </section>
    </div>
  );
}

export default App;
