import React, {useState, useEffect} from 'react';
import axios from 'axios'
import {format} from 'date-fns'
import './App.css';


function App() {
  const [content, setContent] = useState("");
  const [editContent, setEditContent] = useState("");
  const [taskList, setTaskList] = useState([]);
  const [taskId, setTaskId] = useState(null);

  const fetchTasks = async () => {
    const data = await axios.get('/api/tasks');
    const {tasks} = data.data;
    setTaskList(tasks);
  }

  const handleChange = (e, field) => {
    if (field === 'edit') {
      setEditContent(e.target.value)
    } else {
      setContent(e.target.value);
    }
  }

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/tasks/${id}`);
      const updatedList = taskList.filter(task => task.id !== id);
      setTaskList(updatedList);
    } catch (err) {
      console.error(err.message);
    }
  }

  const toggleEdit = (task) => {
    setTaskId(task.id);
    setEditContent(task.content);
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try{
      if (editContent){
        const data = await axios.put(`/api/tasks/${taskId}`, {content: editContent});
        const updatedTask = data.data.task;
        const updatedList = taskList.map(task => {
          if(task.id === taskId){
            return task = updatedTask
          }
          return task
        })
        setTaskList(updatedList)
      } else {
        const data = await axios.post("/api/tasks", {content});
        setTaskList([...taskList, data.data]);
      }
      setContent('');
      setEditContent('');
      setTaskId(null);
    } catch (err) {
      console.error(err.message);
    }
  }

  const handleToggle = async (task) => {
    try {
      await axios.put(`/api/tasks/${task.id}`, {completed: !task.completed});
      const updatedTask = {...task, completed: !task.completed};
      const updatedList = taskList.map((item) => (item.id === task.id ? updatedTask : item));
      setTaskList(updatedList);
    } catch (err) {
      console.error(err.message);
    }
  };


  useEffect(() => {
    fetchTasks();
  }, [])

  return (
    <div className="App">
     <section>
     <form onSubmit={handleSubmit}>
        <label htmlFor='content'>Content</label>
        <input
          onChange={(e) => handleChange(e, 'content')}
          type="text"
          name="content"
          id="content"
          placeholder="Write a task. :)"
          value={content}
        />
        <button type="submit">Submit</button>
     </form>
     </section>
     <section>
      <ul>
        {taskList.map(task => {
          if (taskId === task.id) {
            return (
              <form onSubmit={handleSubmit} key={task.id}>
              <input
                onChange={(e) => handleChange(e, 'edit')}
                type="text"
                name="editContent"
                id="editContent"
                placeholder="Write a task. :)"
                value={editContent}
              />
              <button type='submit'>Submit</button>
              </form>
            )
          } else {
            return (
              <li key={task.id}>
                <input type="checkbox" checked={task.completed} onChange={() => handleToggle(task)} />
                <span>{task.content}</span>
                <button onClick={() => toggleEdit(task)}>Edit</button>
                <button onClick={() => handleDelete(task.id)}>X</button>
              </li>
            )
          }
        })}
      </ul>
     </section>
    </div>
  );
}

export default App;
